import {Container,Form , Button} from 'react-bootstrap'
import React from 'react'
import Task from './../Task/Task'
import axios from 'axios';
import {connection_data} from '../../global'

class TodoList extends React.Component {
    constructor() {
        super()
        this.state = {
            tasks:[],
            newTask:""
        }
        this.addTask = this.addTask.bind(this)
        this.handleTaskChange = this.handleTaskChange.bind(this)
        this.markDone = this.markDone.bind(this)
    }

    getTasks() {
        axios.get(connection_data.backend_host + ":" + connection_data.backend_port + "/tasks")
      .then(res => {
        this.setState({ tasks: res.data });
      })
    }

    componentDidMount() {
        this.getTasks()
    }

    addTask() {
      if (this.state.newTask != "") {
        axios.post(connection_data.backend_host + ":" + connection_data.backend_port +'/newTask', {
          task: this.state.newTask
        });
      }
    }
    
    handleTaskChange(e){
      this.setState({newTask: e.target.value});
   }
    markDone(id) {
      let marked = this.state.tasks.map(task => {if (task.id === id) {task.isDone = !task.isDone}})
      this.setState({tasks:marked})
    }
    render() {
      return (
      <Container>
        <h1>TODO LIST</h1> 
        <Form>
          <Form.Group controlId="formBasicEmail">
            <Form.Control type="text" placeholder="Enter new task" value={this.state.newTask} onChange={this.handleTaskChange}/>
          </Form.Group>
          <Button variant="primary" type="submit" onClick={this.addTask}>
            Submit
          </Button>
        </Form>
          {this.state.tasks.map(task => <Task dataFromParent={task}></Task>)}
      </Container>
        );
    }
  }

  export default TodoList;
