import {Row, Button,OverlayTrigger} from 'react-bootstrap'
import React from 'react'

class Task extends React.Component {
    constructor() {
        super()
        this.state = {
            "task":"",
            "isDone":false,
            "id":"",
            "showRemove":false
        }
        this.markTask = this.markTask.bind(this)
        this.renderButton = this.renderButton.bind(this)
    }

    componentDidMount() {
        this.setState({task:this.props.dataFromParent.task})
        this.setState({isDone:this.props.dataFromParent.isDone})
        this.setState({id:this.props.dataFromParent.id})
    }

    // markTask() { this.props.markDone(this.state.id)}
    // markTask() {
    //     this.setState({"isDone":!this.state.isDone})
    // }

    renderButton = (props) => (
        <Button variant="danger" id="button-tooltip" {...props}>
          Remove
        </Button>
      );

    render() {
      return (
          <Row style={{display: 'flex', justifyContent: 'center'}}>
            <OverlayTrigger
                placement="right"
                delay={{ show: 250, hide: 400 }}
                overlay={this.renderButton}
            >
              <h1 onClick={() => this.props.markTask(this.state.id)}>{this.state.task} {this.state.isDone && "✔" } {this.state.id}</h1>
            </OverlayTrigger>
          </Row>
      );
    }
  }

  export default Task;
