import {Container,Row , Button, Accordion, Card} from 'react-bootstrap'
import React from 'react'
import Task from './../Task/Task'
import axios from 'axios';
import {connection_data} from '../../global'

class TodoList extends React.Component {
    constructor() {
        super()
        this.state = {
            tasks:[]
        }
    }

    getTasks() {
        axios.get(connection_data.backend_host + ":" + connection_data.backend_port + "/tasks")
      .then(res => {
        this.setState({ tasks: res.data });
      })
    }

    componentDidMount() {
        this.getTasks()
    }
    render() {
      return (
      <Container>
        <h1>TODO LIST</h1> 
          {this.state.tasks.map(task => <Task dataFromParent={task}></Task>)}
      </Container>
        );
    }
  }

  export default TodoList;
