import {Container,Form , Button} from 'react-bootstrap'
import React from 'react'
import Task from './../Task/Task'
import axios from 'axios';
import {connection_data} from '../../global'

class TodoList extends React.Component {
    constructor() {
        super()
        this.state = {
            tasks:[],
            newTask:""
        }
        this.addTask = this.addTask.bind(this)
    }

    getTasks() {
        axios.get(connection_data.backend_host + ":" + connection_data.backend_port + "/tasks")
      .then(res => {
        this.setState({ tasks: res.data });
      })
    }

    componentDidMount() {
        this.getTasks()
    }

    addTask() {
      axios.post('/newTask', {
        task: 'Finn',
        lastName: 'Williams'
      });
    }
    
    render() {
      return (
      <Container>
        <h1>TODO LIST</h1> 
        <Form>
          <Form.Group controlId="formBasicEmail">
            <Form.Control type="text" placeholder="Enter new task" />
          </Form.Group>
          <Button variant="primary" type="submit" onClick={thisaddTask}>
            Submit
          </Button>
        </Form>
          {this.state.tasks.map(task => <Task dataFromParent={task}></Task>)}
      </Container>
        );
    }
  }

  export default TodoList;
