import {Row, Button,OverlayTrigger} from 'react-bootstrap'
import React from 'react'

class Task extends React.Component {
    constructor() {
        super()
        this.state = {
            "task":"",
            "isDone":false,
            "id":"",
            "showRemove":false
        }
        this.markTask = this.markTask.bind(this)
        //this.renderTooltip = this.renderTooltip.bind(this)
    }

    componentDidMount() {
        this.setState({task:this.props.dataFromParent.task})
        this.setState({isDone:this.props.dataFromParent.isDone})
        this.setState({id:this.props.dataFromParent.id})
    }

    markTask() {
        this.setState({"isDone":!this.state.isDone})
    }

    // renderTooltip(props) {
    //     <Button id="button-tooltip">
    //       Simple tooltip
    //     </Button>
    // };

    render() {
      return (
          <Row style={{display: 'flex', justifyContent: 'center'}}>
            {/* <OverlayTrigger
                placement="right"
                delay={{ show: 250, hide: 400 }}
                overlay={this.renderTooltip}
            > */}
              <h1 onClick={this.markTask}>{this.state.task} {this.state.isDone && "✔" }</h1>
              <h1> {this.state.id}</h1>
            {/* </OverlayTrigger> */}
          </Row>
      );
    }
  }

  export default Task;
