import {Row} from 'react-bootstrap'
import React from 'react'

class Task extends React.Component {
    constructor() {
        super()
        this.state = {
            "task":"asdad",
            "isDone":false
        }
    }
    markTask() {
        this.setState({isDone:!this.state.isDone})
    }

    render() {
      return (
          <Row>
              <h1 onClick={markTask}>{this.props.dataFromParent.task}</h1>
          </Row>
      );
    }
  }

  export default Task;
